{"compiler":{"version":"0.8.4"},"sources":{"contracts/polygon/PolymorphChildTunnel.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "../tunnel/FxBaseChildTunnel.sol";
import "../base/PolymorphTunnel.sol";
import "../polygon/PolymorphChild.sol";

contract PolymorphChildTunnel is FxBaseChildTunnel, PolymorphTunnel {
    constructor(address _fxChild, address payable _daoAddress)
        FxBaseChildTunnel(_fxChild)
        PolymorphTunnel(_daoAddress)
    {}

    PolymorphChild public polymorphContract;
    uint256 public latestStateId;
    address public latestRootMessageSender;
    bytes public latestData;

    modifier onlyOwner(uint256 tokenId) {
        require(
            polymorphContract.ownerOf(tokenId) == msg.sender,
            "Only owner can move polymorph"
        );
        _;
    }

    function _processMessageFromRoot(
        uint256 stateId,
        address sender,
        bytes memory data
    ) internal override {
        latestStateId = stateId;
        latestRootMessageSender = sender;
        latestData = data;

        (
            uint256 tokenId,
            address ownerAddress,
            uint256 gene,
            bool isVirgin,
            uint256 genomeChanges
        ) = _decodeMessage(data);
        //TODO: Maybe check if person has enough MATIC tokens before that?
        polymorphContract.mintPolymorphWithInfo(tokenId, ownerAddress, gene);
        polymorphContract.wormholeUpdateGene(
            tokenId,
            gene,
            isVirgin,
            genomeChanges
        );
    }

    function moveThroughWormhole(uint256 tokenId)
        external
        override
        onlyOwner(tokenId)
    {
        uint256 gene = polymorphContract.geneOf(tokenId);
        bool isNotVirgin = polymorphContract.isNotVirgin(tokenId);
        uint256 genomeChanges = polymorphContract.genomeChanges(tokenId);
        polymorphContract.burn(tokenId);

        //TODO: Maybe clear gene and genomeChanges
        // It may not be a problem because when we mint on polygon they will be overwritten
        _sendMessageToRoot(
            abi.encode(tokenId, msg.sender, gene, isNotVirgin, genomeChanges)
        );
    }

    function setPolymorphContract(address payable contractAddress)
        public
        onlyDAO
    {
        polymorphContract = PolymorphChild(contractAddress);
    }
}
"},"contracts/tunnel/FxBaseChildTunnel.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// IFxMessageProcessor represents interface to process message
interface IFxMessageProcessor {
    function processMessageFromRoot(
        uint256 stateId,
        address rootMessageSender,
        bytes calldata data
    ) external;
} 

/**
 * @notice Mock child tunnel contract to receive and send message from L2
 */
abstract contract FxBaseChildTunnel is IFxMessageProcessor {
    // MessageTunnel on L1 will get data from this event
    event MessageSent(bytes message);

    // fx child
    address public fxChild;

    // fx root tunnel
    address public fxRootTunnel;

    constructor(address _fxChild) {
        fxChild = _fxChild;
    }

    // Sender must be fxRootTunnel in case of ERC20 tunnel
    modifier validateSender(address sender) {
        require(
            sender == fxRootTunnel,
            "FxBaseChildTunnel: INVALID_SENDER_FROM_ROOT"
        );
        _;
    }

    // set fxRootTunnel if not set already
    function setFxRootTunnel(address _fxRootTunnel) external virtual {
        require(
            fxRootTunnel == address(0x0),
            "FxBaseChildTunnel: ROOT_TUNNEL_ALREADY_SET"
        );
        fxRootTunnel = _fxRootTunnel;
    }

    function processMessageFromRoot(
        uint256 stateId,
        address rootMessageSender,
        bytes calldata data
    ) external override {
        require(msg.sender == fxChild, "FxBaseChildTunnel: INVALID_SENDER");
        _processMessageFromRoot(stateId, rootMessageSender, data);
    }

    /**
     * @notice Emit message that can be received on Root Tunnel
     * @dev Call the internal function when need to emit message
     * @param message bytes message that will be sent to Root Tunnel
     * some message examples -
     *   abi.encode(tokenId);
     *   abi.encode(tokenId, tokenMetadata);
     *   abi.encode(messageType, messageData);
     */
    function _sendMessageToRoot(bytes memory message) internal {
        emit MessageSent(message);
    }

    /**
     * @notice Process message received from Root Tunnel
     * @dev function needs to be implemented to handle message as per requirement
     * This is called by onStateReceive function.
     * Since it is called via a system call, any event will not be emitted during its execution.
     * @param stateId unique state id
     * @param sender root message sender
     * @param message bytes message that was sent from Root Tunnel
     */
    function _processMessageFromRoot(
        uint256 stateId,
        address sender,
        bytes memory message
    ) internal virtual;
}
"},"contracts/base/IPolymorphsTunnel.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

interface IPolymorphsTunnel {
    function moveThroughWormhole(uint256 tokenId) external;
}
"},"contracts/modifiers/DAOControlled.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

abstract contract DAOControlled {
    address payable public daoAddress;

    constructor(address payable _daoAddress) {
        daoAddress = _daoAddress;
    }

    modifier onlyDAO() {
        require(msg.sender == daoAddress, "Not called from the dao");
        _;
    }
}
"},"contracts/base/PolymorphTunnel.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;
import "./IPolymorphsTunnel.sol";
import "../modifiers/DAOControlled.sol";

abstract contract PolymorphTunnel is DAOControlled, IPolymorphsTunnel {
    constructor(address payable _daoAddress) DAOControlled(_daoAddress) {}

    function _decodeMessage(bytes memory data)
        internal
        pure
        returns (
            uint256 tokenId,
            address ownerAddress,
            uint256 gene,
            bool isNotVirgin,
            uint256 genomeChanges
        )
    {
        return abi.decode(data, (uint256, address, uint256, bool, uint256));
    }
}
"},"@openzeppelin/contracts/utils/math/SafeMath.sol":{"content":"// SPDX-License-Identifier: MIT
